import React from "react";
import { Link , useParams} from "react-router-dom"
import { useDispatch , useSelector } from "react-redux"
import { useEffect , useState} from "react";
import { getDetail } from "../actions";
//import styles from "../Styles/DetailRecipe.module.css"
import { Grid,CardMedia, Box, Typography, Divider } from '@mui/material'
import '../styles/Buttons.css';
import NavBar from '../components/NavBar'

export default function DetailRecipe(){
    
    const dispatch = useDispatch()
    const recipeId = useParams()
    const detailRecipe = useSelector((state) => state.detail) //traigo del reducer

    useEffect(() => {
        dispatch(getDetail(recipeId.id))// recipe.Id accedo al id del detalle
    },[dispatch])

//console.log(detailRecipe);
console.log("url de ahora",window.location.pathname);
console.log(recipeId.id);
    return (
        <div>
            {((!detailRecipe)&&(window.location.pathname!==(`/recipe/${recipeId.id}`)))?
                    <div >
                        <h1 >Loading ...</h1>
                    </div> 
                :
                    
                    <div >
                        <NavBar />
                        <Box marginTop='250px' sx={{boxShadow:'rgba(0, 0, 0, 0.35) 0px 5px 15px;',display:'flex',justifyContent:'space-between',flexDirection:{xs:'column',md:'row'},borderRadius:3,alignItems:'center'}}></Box>
                        <Box marginTop='250px'/>
                        <h1 >{detailRecipe?.title}</h1> 
                        <img objectFit='contain' width = "200px"  src={detailRecipe?.imagen} alt="la imagen no se encuentra"/>
                        <h3 >Resumen:</h3>
                        <p >{detailRecipe?.resumen}</p>                         
                        <h3 >Puntuacion</h3>
                        <p >{detailRecipe?.puntuacion}</p>
                        <h3 >Nivel</h3>
                        <p >{detailRecipe?.nivel}</p>
                        <h3 >Tipo de Dietas</h3>
                        <p >{detailRecipe?.dieta?.map(r => (<li >{r.name} </li>))}</p>
                        <h3 >Instrucciones</h3>
                        <p >{detailRecipe?.pasoApaso}</p>
                    </div>
                    
                
            }
        </div>
    )

}
