import * as React from 'react';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
//import Collapse from '@mui/material/Collapse';
//import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
//import { red } from '@mui/material/colors';
 import FavoriteIcon from '@mui/icons-material/Favorite';
 import { Link } from 'react-router-dom';
 import { Grid, Box, Divider, Tooltip } from '@mui/material'
import DietIcons from './IconDiet'
import { useNavigate } from 'react-router-dom';
import ShareIcon from '@mui/icons-material/Share';
import swal from 'sweetalert';
import IconDiet from './IconDiet.jsx'
export default function CardRecipe({ recipe }){
  const [colorHeart, setColorHeart] = React.useState ('black');

  const changeColor = () => { 
    if(colorHeart==="black"){setColorHeart("red")}
    else{ setColorHeart("black")} 
  }

  const navigate=useNavigate()
  return (
    < >
      
        <Box sx={{display:'flex',flexDirection:'row',bgcolor:'#D3CBC9',width:'100%',height:'40vw',marginY:2,boxShadow:'rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset;', border:'1px solid rgb(1,40,83)'}}> 
                <Box sx={{width:'50%'}}>
                    <CardMedia
                      component="img"
                      height='100%'
                      width='100%'
                      image={recipe.image}
                      alt="gf"
                      sx={{objectFit:'cover'}}
                      onClick={()=>{navigate(`/recipe/${recipe.id}`)}}>
                    </CardMedia>
                </Box >

              <Box sx={{display:'flex',flexDirection:'column',width:'100%'}}>
                    <Box sx={{display:'flex',flexDirection:'column',alignItems:'flex-start',mt:1,ml:1}}>
                      <Link to = {'/recipe/'+recipe.id}>
                          <Typography sx={{fontSize:'4.5vw',maxHeight:50, color:'black'}}>{recipe.title.length>100?recipe.title.slice(0,100)+'...':recipe.title}</Typography>
                      </Link>
                    </Box>
              </Box>  
   
              <Box  flexDirection='row' sx={{display:'flex',justifyContent:'center', flexDirection:'row',alignItems:'flex-end'}}>   
                    
                    <IconButton display='flex' alignItems='flex-start' onClick={ changeColor } style={{color: colorHeart}}>
                      <FavoriteIcon fontSize='small'/>
                    </IconButton> 
                    
                    
                    <IconButton display='flex' flexDirection='row'>
                       {recipe.diets.map((diet)=>(<IconDiet title={diet.name}/>))}
                    </IconButton>

                    <IconButton aria-label="share" onClick={() => {navigator.clipboard.writeText(`${window.location.origin}/recipe/${recipe.id}`);swal({
                      title:"URL copiado",
                      text:"Se copio la direcci√≥n de la Receta en el portapapeles",
                      icon:"success",
                      button:"Aceptar"
                    }) }}>
                        <ShareIcon />
                    </IconButton>
              </Box> 
 
        </Box>
      
    </>
  )
}
