import React from 'react';
import { useState, useEffect } from 'react';
import { getTitleRecipes } from '../actions';
import { useDispatch,useSelector} from "react-redux"



export default function SearchBar (){
    const dispatch = useDispatch()
    const [name,setName]=useState("")
    const alerta = useSelector((state) => state.alerta)
   // const recetas = useSelector((state) => state.recipe)


   useEffect(()=>{alert("mensaje")},[alerta])


    //const recetaencontrada = recetasTodas.map(e=>e.title===name)

    function handleInputChange (e){
        e.preventDefault()
        setName(e.target.value)
    }

    function handleSubmit(e){
      if(!name){
        e.preventDefault()
        return alert("el nombre no puede ser vacio")
      }


      /*recetas.find(el=>el===name)
      ?alert("hola")
      :alert("chau")*/

       /* if(!recetaencontrada){
            e.preventDefault()
            return alert("no se encuentra la receta")
        }else{dispatch(getTitleRecipes(name))}*/


        dispatch(getTitleRecipes(name))

    }

    return(
        <div>
            <input
                type = 'text'
                placeholder = "Buscar...."
                onChange = {(e)=> handleInputChange(e)}
                />
                <button type='submit' onClick={(e)=> handleSubmit(e)}> Buscar </button>
        </div>
    )
}
